package sample_0317;

public class rererere {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i;
		
		for(i=1; i<=2; i++){
			for(int j=1; j<=3; j++){
			System.out.print(i);
			System.out.println(j);
			}
		}

	

		for(int b=1;b<=9;b++){
			for(int a=2;a<=9;a++){
				System.out.print(a);
				System.out.print("*");
				System.out.print(b);
				System.out.print("=");
				System.out.print(a*b);
			}
			System.out.println(" ");
		}
	
		for(int k=8; k>=1; k--){
			for(int t=1; t<=9; t++){
				System.out.print(k*1000+t);
				System.out.print(" ");}
			System.out.println("");
			}
		}
		
	}

